package com.decisionmaking;

public class Example2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=11,b=7,c=23;
		if(a>b && a>c)
		{
			System.out.print(a);
		}
		else if(b>a && b>c)
		{
			System.out.print(b);
		}
		else
		{
			System.out.print(c);
		}
	}

}
